<?php		namespace app\Http\controllers\Adminka;		use app\Http\Controllers\Controller;		use Input;		use DB;		class MainController extends Controller 		{			public function __construct()			{				parent::__construct();				$this->middleware('Admin');			}						public function postIndex()			{			    $data = Input::all();				if(isset($data['showhide']))				{					$showhide='show';				}				else				{						$showhide='hide';				}				print_r ($data);				echo $showhide;				exit;				DB::table('products')->insert(					array('name'=>$data['name'],						  'body'=>$data['body'],						  'picture'=>$data['picture'],						  'showhide'=>$showhide,						  'vip'=>$data['vip'],						  'cat_id'=>$data['cat_id']					));					return redirect('/adminka');			}			public function getIndex()			{				$tovars = \App\products::paginate();				return view('adminka.main')->with('tovars',$tovars);			}			public function getEdit ($id=NULL)			{				$tovar = \App\products::find($id);				return view('adminka.edit')->with('tovar',$tovar);			}			public function postEdit($id=NULL)			{	print_r ($_POST);				$tovar = \App\products::find($id);				$data = Input::all();				if(isset($data['showhide']))				{				$tovar->showhide='show';				}else{				$tovar->showhide='hide';				}				$tovar->name=$data['name'];				$tovar->body=$data['body'];				$tovar->price=$data['price'];				if(isset($data['showhide'])){$tovar->showhide='show';}else{$tovar->showhide='hide';}				$tovar->cat_id=$data['category'];				$tovar->vip=$data['vip'];				$tovar->save();				return redirect('/adminka');			}			public function getDelete($id=NULL)			{				$tovar = \App\products::find($id);				$tovar->delete();				return('/adminka');			}			public function getShow($id=NULL)			{				$tovar = \App\products::find($id);				$tovar->showhide='show';				$tovar->save();				return redirect ('/adminka');			}			public function getHide($id=NULL)			{				$tovar = \App\products::find($id);				$tovar->showhide='hide';				$tovar->save();				return redirect ('/adminka');			}		}		?>