<?php		namespace app\Http\controllers\Adminka;		use app\Http\Controllers\Controller;		use Input;		use DB;		class MainController extends Controller 		{			public function __construct()			{				parent::__construct();				$this->middleware('Admin');			}			public function getIndex()			{				return view('adminka.main');			}			public function postIndex()			{			    $data = Input::all();				if(isset($data['showhide']))				{					$showhide='show';				}				else				{						$showhide='hide';				}				print_r ($data);				echo $showhide;				exit;				DB::table('products')->insert(					array('name'=>$data['name'],						  'body'=>$data['body'],						  'picture'=>$data['picture'],						  'showhide'=>$showhide,						  'vip'=>$data['vip'],						  'cat_id'=>$data['cat_id']					));					return redirect('/adminka');			}		}		?>